import { EventEmitter } from "@ombiel/cm-lib";
import clientTools from "@ombiel/aek-lib/lib/client-tools";

export default class Beacons extends EventEmitter {
  constructor(params = {}) {
    super();
    this.on("data error", function () {
      this.off();
    });

    params.cb = this.trigger.bind(this);
    clientTools.invoke("requestBeacons", params);
    
  }
}
