import { EventEmitter } from "@ombiel/cm-lib";
import clientTools from "@ombiel/aek-lib/lib/client-tools";

export default class GPS extends EventEmitter {
  constructor(params = {}) {
    super();
    this.on("location error", function () {
      this.off();
    });

    params.cb = this.trigger.bind(this);
    clientTools.invoke("requestLocation", params);

  }
}
